//   pointer-events: none;
//   min-width: 70px;
// `;



const Header = () => {
  return (

    <>
     <HeaderWrapper>
      <HeaderLogoWrapper>
         <HeaderLogoButton>
           {/* <HeaderLogo src="" alt="logo" /> */}
           <HeaderText>Movies App</HeaderText>
      </HeaderLogoButton>
      </HeaderLogoWrapper>
     </HeaderWrapper>
    </>
  );
};

export default Header;
