//   min-width: 70px;
// `;

const Header = () => {
  return (
    <>
      <HeaderWrapper>
        <HeaderLogoWrapper>
          <HeaderLogoButton>
            {/* <HeaderLogo src="" alt="logo" /> */}
            <HeaderText>Movies App</HeaderText>
          </HeaderLogoButton>
        </HeaderLogoWrapper>
        <Features> Some feature should be here like search or filters</Features>
      </HeaderWrapper>
    </>
  );
};

export default Header;
